xquery version "1.0-ml";

declare function local:maak-kruisvalidatie-rapport(
  $doelen as xs:string*,
  $map as map:map
) as item()*
{
  object-node {
    "identificatie": sem:uuid(),
    "meldingen": array-node {},
    "referentie": sem:uuid(),
    "referentiesTeValideren": array-node {
      for $doel in $doelen
      return object-node {
        "doel": $doel
      }
    }
  }
};

local:maak-kruisvalidatie-rapport("/join/id/slah/di/blah", map:map())

xquery version "1.0-ml";
import module namespace kv = "http://hackaton.com/kruisvalidaties/rapport" at "/ext/kruisvalidaties/rapport.xqy";

declare option xdmp:mapping "false";

declare variable $SPARQL-PREFIX as xs:string := '
PREFIX lvbb: <http://koop.overheid.nl/ontology/lvbb/>
PREFIX imow: <http://koop.overheid.nl/ontology/imow/>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX owl: <http://www.w3.org/2002/07/owl#>
';
declare variable $SPARQL-REFERENTIES-PER-DOEL as xs:string :=  $SPARQL-PREFIX || '
SELECT distinct ?doelId ?regelingId
WHERE {
  ?mow  imow:linkToDoel ?doel ;
        imow:linkToRegelingObject ?regeling .
  ?doel rdf:type lvbb:doel ;
        rdfs:label ?doelId .
  ?versie lvbb:vastgesteldDoorDoel ?doel .
  ?regeling lvbb:heeftVersie ?versie ;
            rdf:type lvbb:Regeling ;
            rdfs:label ?regelingId .
}
';

declare variable $SPARQL-GEOMETRIE-IDENTIFICATIES as xs:string :=  $SPARQL-PREFIX || '
SELECT ?gmlId
WHERE {
  ?aanlevering imow:linkToDoel ?doel ;
      imow:heeftBestand ?bestand .
  ?doel rdfs:label @doelId .
  ?bestand imow:heeftObjectType imow:Gebied ;
           rdfs:label ?naam .
  ?ow imow:bestand ?bestand2 .
  ?bestand2 imow:heeftObjectType imow:Gebied ;
            rdfs:label ?naam ;
            imow:heeftGebied ?gebied .
  ?gebied imow:heeftGeometrieRef ?geometrie .
  ?geometrie rdfs:label ?gmlId
}
';
declare variable $SPARQL-REFERENTIES-PER-REGELING as xs:string :=  $SPARQL-PREFIX || '
SELECT ?regelingId ?wId ?regeltekstId
WHERE {
  ?owAanlevering imow:linkToDoel ?doel ;
                 imow:heeftBestand ?linkId ;
                 imow:linkToRegelingObject ?regeling .
  ?doel rdfs:label @doelId .
  ?regeling rdfs:label @regelingId ;
            rdf:type lvbb:Regeling .
  ?linkId owl:sameAs ?bestandId .
  ?bestandId rdfs:label ?naam ;
             imow:heeftRegeltekst ?regeltekst .
  ?regeltekst rdf:type imow:Regeltekst ;
              rdfs:label ?regeltekstId ;
              imow:linkToWId ?wRef .
  ?wRef rdfs:label ?wId .
}
ORDER BY ?regelingId ?regeltekstId
';

declare function local:maak-rapport(
  $doelen as map:map*,
  $store as sem:store*
) as item()*
{
  object-node {
    "identificatie": sem:uuid(),
    "meldingen": array-node {},
    "referentie": sem:uuid(),
    "referentiesTeValideren": array-node {
      for $doel in $doelen
      let $doelId := map:get($doel, "doelId")
      let $regelingId := map:get($doel, "regelingId")
      let $geometrieIdentificaties :=  sem:sparql($kv:SPARQL-GEOMETRIE-IDENTIFICATIES, $doel, (), $store)
      let $referentiesPerDoel := sem:sparql($kv:SPARQL-REFERENTIES-PER-REGELING, $doel, (), $store)
      return object-node {
        "doel": $doel,
        "referentiesPerDoel": array-node {
          object-node {
            "geometrieIdentificaties": array-node {
              map:get(map:get($geometrieIdentificaties, $doel),"geometrieIdentificaties") ! map:get(., "gmlId")
            },
            "wIdRegeling": $regelingId
          }
        }
      }
    }
  }
};

let $gemeentestad := map:map()
=>map:with("collection", "hackaton/aanlevering")
=>map:with("bindings", map:map()
  =>map:with("akn", "/akn/nl/act/gm0297/2019/reg456"))
let $utrecht := map:map()
=>map:with("collection", "hackaton/utrecht")
=>map:with("bindings", map:map()
  =>map:with("akn", "/akn/nl/act/pv26/2021/3_2RGL-20210519-203227"))
let $roxit := map:map()
=>map:with("collection", "hackaton/roxit")
=>map:with("bindings", map:map()
  =>map:with("akn", "/akn/nl/act/ws0152/2021/Regeling97dce774a5c6414eaf44428ad9ddaf0a"))

let $map := $gemeentestad
let $result := map:map()

let $query :=  cts:collection-query(map:get($map,"collection"))
let $store := (sem:store("document", $query),sem:store("properties", $query))
let $doelen :=  sem:sparql($SPARQL-REFERENTIES-PER-DOEL, (), ("map"), $store)
(:return local:maak-rapport($doelen, $store):)
return (
    $doelen,"--",
    for $doel in $doelen
    let $doelId := map:get($doel, "doelId")
    let $regelingId := map:get($doel, "regelingId")
    let $geometrieIdentificaties :=  sem:sparql($SPARQL-GEOMETRIE-IDENTIFICATIES, $doel, (), $store)
    let $referentiesPerDoel := sem:sparql($SPARQL-REFERENTIES-PER-REGELING, $doel, (), $store)
    return (
      $doel,
      $geometrieIdentificaties,
      $referentiesPerDoel,
      $regelingId
    )
)
(:
return (
object-node {
  "identificatie": sem:uuid(),
  "meldingen": array-node {},
  "referentie": sem:uuid(),
  "referentiesTeValideren": array-node {
    for $doel in $doelen
    let $doelId := map:get($doel, "doelId")
    let $regelingId := map:get($doel, "regelingId")
    let $geometrieIdentificaties :=  sem:sparql($SPARQL-GEOMETRIE-IDENTIFICATIES, $doel, (), $store)
    let $referentiesPerDoel := sem:sparql($SPARQL-REFERENTIES-PER-REGELING, $doel, (), $store)
    return object-node {
      "doel": $doelId,
      "referentiesPerDoel": array-node {
        object-node {
          "geometrieIdentificaties": array-node { $geometrieIdentificaties ! map:get(., "gmlId") },
          "referentiesPerRegeling": array-node { $referentiesPerDoel ! object-node { "regeltekstId": map:get(., "regeltekstId"), "wId": map:get(., "wId") } },
          "wIdRegeling": $regelingId
        }
      }
    }
  }
}
):)

